        public static float calcDaleChall(List<string> words,int sentenceNo)
        {
            List<string> difficultWords = new List<string>();
            foreach (string word in words)
            {
                if(!Array.Exists(listOf3000,delegate(string s) { return s.Equals(word); }))
                {
                    difficultWords.Add(word);
                }

            }
            return 0.1579f*(100.0f*difficultWords.Count/words.Count)+0.0496f*(words.Count/sentenceNo);
        }

        
    }
